mapper.select:
SELECT * FROM ( SELECT * FROM oa_flow LEFT JOIN flow_car_meeting AS fcm ON oa_flow.id = fcm.flow_id
AND concat_ws(",", oa_flow.flow_name, oa_flow.launch_name, oa_flow.approval_name) LIKE '%' #{key, jdbcType=VARCHAR} '%'
AND oa_flow.launch_id = #{loginId, jdbcType=INTEGER}
GROUP BY fcm.use_car_record_id HAVING MAX(oa_flow.create_time) UNION SELECT * FROM oa_flow LEFT JOIN flow_car_meeting AS fcm ON oa_flow.id = fcm.flow_id
AND concat_ws(",", oa_flow.flow_name, oa_flow.launch_name, oa_flow.approval_name) LIKE '%' #{key, jdbcType=VARCHAR} '%'
AND oa_flow.launch_id = #{loginId, jdbcType=INTEGER}
GROUP BY fcm.meeting_id HAVING MAX(oa_flow.create_time)) AS r ORDER BY r.update_time limit #{start, jdbcType=INTEGER}, #{pageSize, jdbcType=INTEGER}

mapper.delete:
DELETE FROM oa_flow WHERE id in
( SELECT r.id FROM
  (SELECT f.id AS id from oa_flow AS f LEFT JOIN flow_car_meeting AS fcm ON f.id = fcm.flow_id
     WHERE
      fcm.use_car_record_id = #{id, jdbcType=INTEGER}
      fcm.meeting_id = #{id, jdbcType=INTEGER}
  ) AS r
);
DELETE FROM flow_car_meeting WHERE
use_car_record_id = #{id, jdbcType=INTEGER}
meeting_id = #{id, jdbcType=INTEGER};
